import { useState } from "react"
import axios from "axios"
import Carousel from "./Carousel"
import Getproducts from './Getproducts';


const Home=()=>{
    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')
    const [phone,setPhone]=useState('')
    const [payment_option,setPaymentoption]=useState('')
    const [category,setCategory]=useState('')
    // 3 states of posting data 
    const [loading,setLoading]=useState('')
    const [success,setSuccess]=useState('')
    const [error,setError]=useState('')

    const submit=async(e)=>{
        e.preventDefault()
        setLoading('please wait...')
        try {
            const data= new FormData()
            data.append("email",email)
            data.append("password",password)
            data.append("phone",phone)
            data.append("payment_option",payment_option)
            data.append("category",category)

            const response=await axios.post("https://teekay.pythonanywhere.com/api/create_shop",data)
            setLoading("")
            setSuccess("shop created successfully")


        } catch (error) {
            setLoading("")
            setError('something went wrong!')
        }

    }
    return(
      <div className="row ">

        <div className="row"> <div className="col-md-4 card shadow bg-light ">

          </div>   
        <div className="col-md-12"> <Carousel/> </div></div>




        {/* Shop Creation Form Section */}
        <div className="row justify-content-center">
          <div className="card shadow col-md-6 mt-5 p-4 signup text-light">
            <Getproducts/>
          </div>
        </div>
      </div>

    )
}
export default Home
