import React from 'react'
import { Link, useLocation } from 'react-router-dom'
import { useState } from 'react'
import axios from 'axios'
const Makepayment = () => {
      const {product} = useLocation().state || {};

      const [phone,setPhone]=useState('')
      const [message, setMessage] = useState("")
  // 3 states of posting data
  const [loading,setLoading]=useState("")
  const [error,setError]=useState("")
  const [success,setSuccess]=useState("")

  // function to make payment 
 const submit=async (e)=>{
  e.preventDefault()
  if (!phone.startsWith("254")) {
    setError("Phone number must start with 254");
    return;
  }
  setError(""); // Clear any previous error
  setLoading("please wait as we process your request")
  try {
   const data=new FormData()
   data.append("amount",product.product_cost)
   data.append("phone",phone)
   const response=await axios.post("https://teekay.pythonanywhere.com/api/mpesa_payment",data)
   setSuccess("complete the payment on your end")
   setLoading("")
  } catch (error) {
    setLoading("")
    setError(error.message)
  }
 }
  const image_url="https://alexmuuo815.pythonanywhere.com/static/images/"

  return (
    <div className='row justify-content-center  '>
      <div className='card shadow col-md-3'>  <img src={image_url+product.product_photo} alt="" className="mt-4 product_img" /></div>
    <div className='card shadow col-md-4'>

      <h1 className='text-center'>Make payment -Lipa na Mpesa</h1>

      <h5 className="text-info mt-2">{product.nameofshop}</h5>
            <p className="text-muted">Shop Category: {product.category}</p>
            <b className="text-muted">Product Name: {product.product_name}</b>
            <p className="text-muted">Description: {product.product_description}</p>
            <p className="text-muted">Stock Quantity: {product.stock_quantity}</p>
            <b className="text-warning">Product Cost: {product.product_cost}</b>


      {/* bind loading  */}
      {loading}
      {/* bind success  */} <br />
     <h6 className='text-success'> {success}</h6>
      {/* bind error  */}
      <h6 className='text-danger'>{error}</h6>
      <form onSubmit={submit}>
        <input type="number" placeholder='Enter your phone 254XXXXXXXXX' className='form-control' onChange={(e)=>setPhone(e.target.value)} required />
        {/* {phone} */}
         <br />
        <button className='btn btn-dark mt-2 w-100'>Pay Now</button>
      </form>
      </div>
      </div>
  )
}

export default Makepayment
