import axios from "axios";
import { useState, useEffect } from "react";
import "../App.css";
import { useNavigate } from 'react-router-dom';

const Getproducts = () => {
  const navigate = useNavigate();

  // State to store products, loading state, and error message
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState("");
  const [error, setError] = useState("");

  // Function to fetch products from the backend
  const getProducts = async () => {
    setLoading("Please wait ...");
    setError("");  // Reset error state before fetching

    try {
      const response = await axios.get(
        "https://teekay.pythonanywhere.com/api/get_electronics"
      );
      setLoading(""); // Reset loading state
      setProducts(response.data); // Set the products data from the response
    } catch (err) {
      setLoading(""); // Reset loading state
      setError("Failed to fetch products. Please try again later."); // Set error message
    }
  };

  // Call the function when the component mounts
  useEffect(() => {
    getProducts();
  }, []);

  // Log to see if we have products (you can remove this in production)
  console.log(products);

  // Search filter state
  const [searchTerm, setSearchTerm] = useState('');

  // Function to filter products based on name, specifications, and brand
  const filteredProducts = products.filter(product => {
    const lowerCaseSearchTerm = searchTerm.toLowerCase();
    return (
      (product.product_name && product.product_name.toLowerCase().includes(lowerCaseSearchTerm)) ||
      (product.specifications && product.specifications.toLowerCase().includes(lowerCaseSearchTerm)) ||
      (product.brand && product.brand.toLowerCase().includes(lowerCaseSearchTerm))
    );
  });

  // Image URL for product photos
  const image_url = "https://teekay.pythonanywhere.com/static/images/";

  return (
    <div className="row container-fluid">
      <div className="card shadow bg-white">
        <h1 className="text-center text-info">Available Products</h1>
      </div>
      <p className="text-center text-info">{loading}</p>
      <p className="text-center text-danger">{error}</p>  

      {/* Search input */}
      <div className="d-flex justify-content-center mb-3">
        <input
          type="text"
          placeholder="Search by name, specifications, or brand ..."
          className="w-full border p-2 rounded"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="row mt-4">
        {/* If no products match the search, show a message */}
        {filteredProducts.length === 0 && (
          <p className="text-center text--500">No products found.</p>
        )}

        {/* Mapping through filtered products */}
        {filteredProducts.map((product) => (
          <div className="col-md-3 justify-content-center mb-4" key={product.product_id}>
            <div className="card shadow p-2">
              {/* Image of the product */}
              <img
                src={image_url + product.electronic_photo}
                alt={product.product_name} // Added alt text for accessibility
                className="mt-4 product_img"
              />
              <div className="card-body">
                <h5 className="text-info mt-2">{product.nameofshop}</h5>
                <p className="text-dark">Brand: {product.brand}</p>
                <p className="text-dark">Product Name: {product.product_name}</p>
                <p className="text-dark">Specifications: {product.specifications}</p>
                <p className="text-dark">Stock Quantity: {product.stock_quantity}</p>
                <b className="text-dark">Product Price: {product.product_cost}</b>

                <br />
                <button
                  className="btn btn-dark mt-2 w-100"
                  onClick={() => navigate("/makepayment", { state: { product } })}
                >
                  Purchase Now
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Getproducts;
