import axios from "axios";
import { useState, useEffect } from "react";
import "../App.css";
import { Link, useNavigate } from 'react-router-dom'


const Getshops = () => {
  const navigate = useNavigate();


  // Function to get products
  const [products, setproducts] = useState([]);
  const [loading, setLoading] = useState("");
  const [error, setError] = useState("");

  const getproducts = async () => {
    setLoading("please wait ...");
    try {
      const response = await axios.get(
        "https://alexmuuo815.pythonanywhere.com/api/get_shops"
      )
      setLoading("");
      setproducts(response.data);
    } catch (error) {

    }
  };

  // Call the function
  useEffect(() => {
    getproducts();
  }, []);

  // Log to see if we have products
  console.log(products);

  // provide the image url from python anywhere
  const image_url =   "https://alexmuuo815.pythonanywhere.com/static/images/";
    const [searchTerm, setSearchTerm] = useState('');

    const drones = [
      {products},

    ];


    const filteredDrones = products.filter(product =>
      product.product_name && product.product_name.toLowerCase().includes(searchTerm.toLowerCase())
    );

  return (
    <div className="row container-fluid ">

     <div className="card shadow bg-white "> <h1 className="text-center text-info">Available shops</h1></div>
     <p className="text-center text-info">{loading}</p>
      <p className="text-center text-danger">{error}</p>  
      <div className="">
      <input type="text" placeholder="Search ..." className="w-full border p-2 rounded"  value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
      />

      <div className="">
        {filteredDrones.map(product => (
          <div key={product.product_id} className="border p-3 ">
            <h2 className="text-lg font-semibold">{product.product_name}</h2>
            <p>{product.product_cost}</p>
          </div>
        ))}

        {filteredDrones.length === 0 && (
          <p className="text-gray-500">Product not found.</p>
        )}
      </div>
    </div>
      {/* mapping here */}
      {products.map((product) => (
        <div className="col-md-3 justify-content-center mb-4" >
          <div className="card shadow p-2">
            {/* we will have an image here */}
            <img
              src={image_url + product.product_photo}
              alt={product.nameofshop} // Added alt text for accessibility
              className="mt-4 product_img"
            />
            <div className="card-body text-center">
            <h5 className="text-info mt-2">{product.nameofshop}</h5>
            <p className="text-muted">product Category: {product.category}</p>
            <p className="text-muted">Product Name: {product.product_name}</p>
            <p className="text-muted">Description: {product.product_description}</p>
            <p className="text-muted">Stock Quantity: {product.stock_quantity}</p>
            <b className="text-primary">Product Cost: {product.product_cost}</b>

              <br />
              <button
                className="btn btn-dark mt-2 w-100"
                onClick={() => navigate("/makepayment", { state: { product } })}
              >
                Purchase Now
              </button>
            </div>
          </div>
        </div>
      ))}
      {/* end of mapping here */}
    </div>
  );
};

export default Getshops
