import { useState } from "react";
import axios from "axios";
import Carousel from "./Carousel";
import Getproducts from './Getproducts';
import { Spinner } from 'react-bootstrap'; // Import Spinner from React-Bootstrap

const Home = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [phone, setPhone] = useState('');
    const [payment_option, setPaymentoption] = useState('');
    const [category, setCategory] = useState('');
    const [loading, setLoading] = useState('');
    const [success, setSuccess] = useState('');
    const [error, setError] = useState('');

    const submit = async (e) => {
        e.preventDefault();
        setLoading('please wait...');
        try {
            const data = new FormData();
            data.append("email", email);
            data.append("password", password);
            data.append("phone", phone);
            data.append("payment_option", payment_option);
            data.append("category", category);

            const response = await axios.post("https://teekay.pythonanywhere.com/api/create_shop", data);
            setLoading(""); // Clear the loading state
            setSuccess("shop created successfully");
        } catch (error) {
            setLoading(""); // Clear the loading state
            setError('something went wrong!');
        }
    };

    return (
        <div className="row">
            {/* Conditionally render the spinner based on loading state */}
            {loading && (
                <div className="spinner-container">
                    <Spinner animation="border" variant="primary" />
                    <p>{loading}</p>
                </div>
            )}

            <div className="row">
                <div className="col-md-4 card shadow bg-light">
                    {/* Form or content */}
                </div>
                <div className="col-md-12">
                    <Carousel />
                </div>
            </div>

            {/* Shop Creation Form Section */}
            <div className="">
                <div className="">
                    <Getproducts />
                </div>
            </div>
        </div>
    );
}

export default Home;
