import axios from "axios";
import { useState, useEffect } from "react";
import "../App.css";
import { Link, useNavigate } from 'react-router-dom';

const Getproducts = () => {
  const navigate = useNavigate();

  // State to store products, loading state, and error message
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState("");
  const [error, setError] = useState("");

  // Function to fetch products from the backend
  const getProducts = async () => {
    setLoading("Please wait ...");
    setError("");  // Reset error state before fetching

    try {
      const response = await axios.get(
        "https://teekay.pythonanywhere.com/api/get_electronics"
      );
      setLoading(""); // Reset loading state
      setProducts(response.data); // Set the products data from the response
    } catch (err) {
      setLoading(""); // Reset loading state
      setError("Failed to fetch products. Please try again later."); // Set error message
    }
  };

  // Call the function when the component mounts
  useEffect(() => {
    getProducts();
  }, []);

  // Log to see if we have products (you can remove this in production)
  console.log(products);

  // Search filter state
  const [searchTerm, setSearchTerm] = useState('');

  // Filter products based on the search term
  const filteredProducts = products.filter(product =>
    product.product_name && product.product_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Image URL for product photos
  const image_url = "https://teekay.pythonanywhere.com/static/images/";

  return (
    <div className="row container-fluid">
      <div className="card shadow bg-white">
        <h1 className="text-center text-info">Available Products</h1>
      </div>
      <p className="text-center text-info">{loading}</p>
      <p className="text-center text-danger">{error}</p>  

      <div className="d-flex justify-content-center">
        <input
          type="text"
          placeholder="Search ..."
          className="w-full border p-2 rounded"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />

        <div className="">
          {filteredProducts.map(product => (
            <div key={product.product_id} className="border p-3">
              <h2 className="text-lg font-semibold">{product.name}</h2>
              <p>{product.product_cost}</p>
            </div>
          ))}

          {filteredProducts.length === 0 && (
            <p className="text-gray-500">Product not found.</p>
          )}
        </div>
      </div>

      {/* Mapping through products */}
      {products.map((product) => (
        <div className="col-md-3 justify-content-center mb-4" key={product.product_id}>
          <div className="card shadow p-2">
            {/* Image of the product */}
            <img
              src={image_url + product.product_photo}
              alt={product.nameofshop} // Added alt text for accessibility
              className="mt-4 product_img"
            />
            <div className="card-body text-center">
              <h5 className="text-info mt-2">{product.name}</h5>
              <p className="text-muted">Product Category: {product.category}</p>
              <p className="text-muted">Product Name: {product.product_name}</p>
              <p className="text-muted">Description: {product.product_description}</p>
              <p className="text-muted">Stock Quantity: {product.stock_quantity}</p>
              <b className="text-primary">Product Cost: {product.product_cost}</b>

              <br />
              <button
                className="btn btn-dark mt-2 w-100"
                onClick={() => navigate("/makepayment", { state: { product } })}
              >
                Purchase Now
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Getproducts;
