import React from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useState } from 'react'
import axios from 'axios'


const Signin = () => {
    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')
    const [success,setSuccess]=useState('')
    const [loading,setLoading]=useState('')
    const [error,setError]=useState('')
    const navigate = useNavigate(); // Initialize navigate function

    const submit=async (e)=>{
        e.preventDefault()
        setLoading(true);
        setError(null);
        // setLoading("Please wait..")

        try {
            const data=new FormData()
            data.append("email",email)
            data.append("password",password)

            const response = await axios.post('https://teekay.pythonanywhere.com/api/signin', data)
            // setLoading('')
            setLoading(false);
            if (response.data && response.data.success) { // Check if response indicates success

      // ðŸ”½ ADDED: Store user in localStorage and redirect
      if (response.data.user) {
        localStorage.setItem("user", JSON.stringify(response.data.user));
        navigate("/home");
      } else {
        setError(response.data.message || "Login failed");
      }
      // ðŸ”¼ END ADDED
            } else {
                setError(response.data.message || "Invalid credentials") // Handle invalid data
            }
        } catch (error) {
            setLoading('')
            setError("")
        }
    }

    return (
        <div className="row justify-content-center mt-5">
            <div className="card shadow col-md-4 p-4 ">
            <div> <h1 className='text-center bsignup'>Sign In</h1>
                <h4 className="text-center text-info well">Welcome To Juju</h4></div>
                <form onSubmit={submit}>
                    <h5 className="text-info text-sm"> {loading} </h5>
                    <h5 className="text-info">{success}</h5>
                    <h5 classNameName="text-danger">{error}</h5>
                    <input type="email" placeholder='Enter email ' required className='form-control' onChange={(e)=>setEmail(e.target.value)}/> 
                    <br />
                    <input type="password" placeholder='Enter password' required className='form-control' onChange={(e)=>setPassword(e.target.value)}/>
                    <br /><br />
                    <button type='submit' className='btn btn-primary w-100'>Sign In</button><br /><br />
                    <p className='text-dark'>Don't have an account? <Link to='/'  className="text-dark">Sign Up</Link></p>
                </form>
            </div>
        </div>
    )
}

export default Signin
